import { StyleSheet } from 'react-native';

const styles = StyleSheet.create({
  avatar: {
    width: 100,
    height: 60,
    borderRadius: 100,
    marginTop: 10
  },
  header: {
    alignItems: 'left',
    justifyContent: 'right',
  },
  name: {
    fontSize: 24,
    fontWeight: 'bold',
    marginTop: 7,
  },
  role: {
    marginBottom: 5,
  }
});

export default styles;
