import React, { Component } from 'react';

export default class ControlledForm extends Component {
    constructor(props) {
        super(props);
        this.state = {
            nome: '', idade: ''
        };
    }

    handleOnNomeChange = (e) => {
        this.setState({ nome: e.target.value });
    }

    handleOnIdadeChange = (e) => {
        this.setState({ idade: e.target.value });
    }

    handleSubmit = (e) => {
        e.preventDefault();
        alert('Nome: ' + this.state.nome);
    }

    render() {
        const { nome, idade } = this.state;
        return (
            <form onSubmit={this.handleSubmit}>
                <label>
                    Nome:
                    <input type="text" name="nome" value={nome} onChange={this.handleOnNomeChange}/>
                </label>
            break
            <label>

                    Idade:
                    <input type="number" value={idade} onChange={this.handleOnIdadeChange}/>
                </label>
            <label>
                    Curso:
                    <input type="text" name="nome" value={nome} onChange={this.handleOnNomeChange}/>
                </label>
