import React, { Component } from 'react';
import { ActivityIndicator, FlatList, Text, View, StyleSheet, TouchableOpacity, ScrollView } from 'react-native';

export default class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      data: [],
      isLoading: true
    };
  }

  async getMovies() {
    try {
      const response = await fetch('https://reactnative.dev/movies.json');
      const json = await response.json();
      this.setState({ data: json.movies });
    } catch (error) {
      console.log(error);
    } finally {
      this.setState({ isLoading: false });
    }
  }

  componentDidMount() {
    this.getMovies();
  }

  render() {
    const { data, isLoading, flexDirection } = this.state;

    return (
         <ScrollView contentContainerStyle={style.contentContainer}>

      <View style={{ flex: 1, padding: 24, backgroundColor:"blue",flexDirection: "column" }}>
        {isLoading ? <ActivityIndicator/> : (
          <FlatList
            data={data}
            keyExtractor={({ id }, index) => id}
            renderItem={({ item }) => (
              <Text>{item.title}, {item.releaseYear}</Text>
            )}
          />
        )}
      </View>
</ScrollView>
    );
const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
  },
     contentContainer: {
    paddingVertical: 20
  }
});
  }


};
