import React, { Component } from 'react';

export default class ControlledForm extends Component {
    constructor(props) {
        super(props);
        this.state = {
            nome: '', idade: '', matricula: '', bairro:'', complemento:'', cidade:'', estado:'', curso:'', endereco: '', cep: ''
        };
    }

    handleOnComplementoChange = (e) => {
        this.setState({ complemento: e.target.value });
    }
     handleOnMatriculaChange = (e) => {
        this.setState({ matricula: e.target.value });
    }
     handleOnCursoChange = (e) => {
        this.setState({ curso: e.target.value });
    }
    handleOnNumeroChange = (e) => {
        this.setState({ numero: e.target.value });
    }

    handleOnNomeChange = (e) => {
        this.setState({ nome: e.target.value });
    }

    handleOnBairroChange = (e) => {
        this.setState({ bairro: e.target.value });
    }

    handleOnCidadeChange = (e) => {
        this.setState({ cidade: e.target.value });
    }

    handleOnIdadeChange = (e) => {
        this.setState({ idade: e.target.value });
    }

    handleOnEstadoChange = (e) => {
        this.setState({ estado: e.target.value });
    }
     handleOnEnderecoChange = (e) => {
        this.setState({ estado: e.target.value });
    }
     handleOnCEPChange = (e) => {
        this.setState({ cep : e.target.value });
    }

    handleSubmit = (e) => {
        e.preventDefault();
        alert('Nome: /n' + this.state.nome + 'Idade: /n' + this.state.idade + 'CPF: /n' + this.state.cpf + 'Matricula: /n' + this.state.matricula + 'Curso: /n' + this.state.curso + 'Endereco: /n' + this.state.endereco + 'Numero: /n' + this.state.numero + 'Complemento: /n' + this.state.complemento + 'Bairro: /n' + this.state.bairro + 'Cidade: /n' + this.state.cidade + 'Estado: /n' + this.state.estado + 'CEP: /n' + this.state.cep);
    }
    render() {
        const { nome, idade, matricula, bairro, complemento, cidade, estado, endereco, curso, cep } = this.state;
        return (
            <form onSubmit={this.handleSubmit}>
                <label>
                    Nome:
                    <input type="text" name="nome" value={nome} onChange={this.handleOnNomeChange}/>
                </label>

                 <label>
                    Matricula:
                    <input type="text" name="matricula" maxlength="9" value={matricula} onChange={this.handleOnMatriculaChange}/>
                </label>

            <label>

                    Idade:
                    <input type="number" value={idade} onChange={this.handleOnIdadeChange}/>
                </label>
            <label>
                    Curso:
                    <input type="text" name="nome" value={curso} onChange={this.handleOnCursoChange}/>
                </label>
            <label>
                    Endere√ßo:
                    <input type="text" name="nome" value={endereco} onChange={this.handleOnEnderecoChange}/>
                </label>
             <label>
                    Cidade:
                    <input type="text" name="nome" value={cidade} onChange={this.handleOnCidadeChange}/>
                </label>
                 <label>
                    Complemento:
                    <input type="text" name="nome" value={complemento} onChange={this.handleOnComplementoChange}/>
                </label>
            <label>
                    Bairro:
                    <input type="text" name="nome" value={bairro} onChange={this.handleOnBairroChange}/>
                </label>
             <label>
                    cep:
                    <input type="number" name="nome" value={cep} onChange={this.handleOnCEPChange}/>
                </label>
            <label for="estados-seletor">Escolha seu estado:</label>

<select name="estados" id="estados-seletor">
    <option value="">Estados</option>
    <option value="rondonia">RO</option>
    <option value="acre">AC</option>
    <option value="amazonas">AM</option>
    <option value="roraima">RR</option>
    <option value="para">PA</option>
    <option value="amapa">AP</option>
    <option value="tocantis">TO</option>
    <option value="maranhao">MA</option>
    <option value="piau">PI</option>
    <option value="ceara">CE</option>
    <option value="rio grando do norte">RN</option>
    <option value="paraiba">PB</option>
    <option value="pernambuco">PE</option>
    <option value="alagoas">AL</option>
    <option value="sergipe">SE</option>
    <option value="bahia">BA</option>
    <option value="minas gerais">MG</option>
    <option value="espirito santo">ES</option>
    <option value="rio de janeiro">RJ</option>
    <option value="sao paulo">SP</option>
    <option value="parana">PR</option>
    <option value="santa catarina">SC</option>
    <option value="rio grando do sul">RS</option>
    <option value="mato grosso do sul">MS</option>
    <option value="mato grosso">MT</option>
    <option value="goias">GO</option>
    <option value="distrito federal">DF</option>
</select>



                <input type="submit" value="Login"/>
            </form>
        );
    }
}
